let days, inDays, years, months, yearOut, monthOut, dayOut, output;

const testData = () => {
  days = Number(document.querySelector('#xbox').value);
  inDays = days;
  years = Math.floor(days/365);
  days = days - years * 365;
  months = Math.floor(days / 30);
  days = days - months * 30;

  yearOut = years > 0 ? `${years} år, ` : '';

  if (months > 0) {
    monthOut = months.toString() + (months > 1 
    ? ' månader, ' 
    : ' månad, '); 
  }
  else monthOut = '';

  dayOut = (yearOut !== '' || monthOut !== ''
  ? 'och ' : '') 
  + days.toString() 
  + (days !== 1 ? ' dagar.' : ' dag.');

  output = `${inDays} dagar motsvarar ${yearOut}${monthOut}${dayOut}`;

  document.querySelector('#printout').innerHTML = `<p>${output}</p>`;
};

